INSERT INTO product (id, city, description, filename, colors, product_type, product_title, producer, price, type, year, product_rating) VALUES (1, 'İstanbul', '1970lerde Nike yeni bir ayakkabı markasıydı. Bu dönemde hala basketbol alanında ilerlemeye çalışıyor ve prototiplerimizi yerel takımımızda deniyorduk. Tabii ki tasarım yıllar içinde değişti ama ad aynı kaldı. Nike Blazer Mid 77 Vintage, satışa sunulduğundan beri klasik bir stildir.', 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/389b709e-5102-4e55-aa5d-07099b500831/blazer-mid-77-vintage-ayakkabısı-CBDjT0.png', 'Beyaz, Kırmızı, Siyah', 'Giyim', 'Nike Blazer Mid 77 Vintage', 'Nike', 350, 'Erkek Ayakkabı', 2023, 0);
